document.addEventListener("DOMContentLoaded",function(){var n=document.querySelectorAll(".hero__indicators-dot"),a=document.querySelector(".hero__slides");n.forEach(function(t,e){t.addEventListener("click",function(){a.style.transform="translateX(-".concat(34*e,"%)"),n.forEach(function(t){return t.classList.remove("active")}),t.classList.add("active")})}),n[0].classList.add("active"),a.style.transform="translateX(0%)"});